import { React, useEffect, useState } from "react";
import "./App.css";

// The function that makes the fetch request to the Products API
import { getProducts } from "./services/getProducts";

function App() {
  // use the products variable to read all of your products
  // and display them on your page
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const loadData = async () => {
      const products = await getProducts();
      setProducts(products);
    };

    loadData();
  }, []);

  const formatMoney = (number) => {
    const formattedNumber = number / 100;
    return formattedNumber.toLocaleString("en-US", {
      style: "currency",
      currency: "USD",
    });
  };

  return (
    <div className="container">
      <h1>Authori-Tee</h1>
      <div className="product-grid">
        {products.map((product) => (
          <div key={product.id}>
            <img
              className="image"
              src={product.images[0]}
              alt={product.description}
            />
            <h2>{product.name}</h2>
            <p>{product.description}</p>
            {product.prices.map((price) => (
              <div>{formatMoney(price.unit_amount)}</div>
            ))}
            <button className="button-class">Buy Now</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
